import React from "react";
import "../style/basket.css";
import Header from "../../MainLanding/pages/Header";
import Footer from "../../MainLanding/pages/Footer";
import { CustomContext } from "../../utils/Context";
import { useContext } from "react";

function Basket() {
  const { favored, setFavored } = useContext(CustomContext);
  console.log(favored);

  return (
    <>
      <Header />
      <div className="basket-place container">
        <div className="basket-place__title-content">
          <div className="basket-place__title">
            <h2>Ваша корзина</h2>
            <span>{favored.length} товара</span>
          </div>
          <div className="basket-place__tools">
            <span className="basket-place__tools">Продолжить покупки</span>
            <span className="basket-place__tools">Очистить корзину</span>
          </div>
        </div>
        <div className="basket-place__content"></div>
      </div>
    </>
  );
}

export default Basket;
