import React, { useContext, useState } from "react";
import "./style/catalog.css";
import iphone from "../../image/products/iphone.png";
import mac from "../../image/products/mac.png";
import ipad from "../../image/products/ipad.png";
import tea from "../../image/products/tea.png";
import home from "../../image/products/home.png";
import xbox from "../../image/products/xbox.png";
import whatch from "../../image/products/whatch.png";
import pc from "../../image/products/pc.png";

function Сatalog() {
  const запрос = new XMLHttpRequest();
  const URL_запроса = "http://localhost:3000/actualProduct";

  запрос.open("GET", URL_запроса, true);

  запрос.onreadystatechange = function () {
    if (запрос.readyState === 4 && запрос.status === 200) {
      const данные = JSON.parse(запрос.responseText);
      console.log(данные);
      // Действия с полученными данными
    } else if (запрос.readyState === 4 && запрос.status !== 200) {
      console.error("Ошибка получения списка:", запрос.status);
    }
  };

  запрос.send();
  function handleClick(href) {
    fetchData();
    console.log(products);
    window.location.href = `${href}`;
  }

  return (
    <section className="catalog">
      <h2>Каталог товаров</h2>
      <div className="catalog__list">
        <div
          className="catalog__list_item"
          onClick={() => handleClick("/phone")}
        >
          <span>Смартфоны</span>
          <img src={iphone} alt="404" />
        </div>
        <div
          className="catalog__list_item"
          onClick={() => handleClick("/laptop")}
        >
          <span>Планшеты</span>
          <img src={ipad} alt="404" />
        </div>
        <div
          className="catalog__list_item"
          onClick={() => handleClick("/tablet")}
        >
          <span>Ноутбуки</span>
          <img src={mac} alt="404" />
        </div>
        <div
          className="catalog__list_item"
          onClick={() => handleClick("/home")}
        >
          <span>Умный дом</span>
          <img src={home} alt="404" />
        </div>
        <div
          className="catalog__list_item"
          onClick={() => handleClick("/console")}
        >
          <span>Игровые приставки</span>
          <img src={xbox} alt="404" />
        </div>
        <div
          className="catalog__list_item"
          onClick={() => handleClick("/clock")}
        >
          <span>Умные часы</span>
          <img src={whatch} alt="404" />
        </div>
        <div
          className="catalog__list_item"
          onClick={() => handleClick("/appliances")}
        >
          <span>Бытовая техника</span>
          <img src={tea} alt="404" />
        </div>
        <div
          className="catalog__list_item"
          onClick={() => handleClick("/computer")}
        >
          <span>Компьютеры</span>
          <img src={pc} alt="404" />
        </div>
      </div>
    </section>
  );
}

export default Сatalog;
