import React from "react";
import Header from "../MainLanding/pages/Header";
import { useContext } from "react";
import { CustomContext } from "../utils/Context";
import { useNavigate } from "react-router-dom";
import "./style/personal.css";
import avatar from "../MainLanding/image/icone/avatar.png";

function PersonalArea() {
  const { user, setUser } = useContext(CustomContext);

  const navigate = useNavigate();

  const logOutUser = () => {
    setUser({
      email: "",
    });
    localStorage.removeItem("user");
    navigate("/");
  };

  return (
    <>
      <Header />
      <section className="personal container">
        <div className="personal__pofile">
          <div className="personal__pofile-date">
            <img src={avatar} alt="404" />
            <div className="pofile-date__txt">
              <span>{user.name}</span>
              <span>{user.email}</span>
            </div>
          </div>
          <ul className="personal__pofile-list">
            <li className="personal__pofile-list_item">
              {" "}
              <img src={avatar} alt="404" />
              <span></span>Мои заказы
            </li>
            <li className="personal__pofile-list_item">
              <img src={avatar} alt="404" />
              Список желаемого
            </li>
            <li className="personal__pofile-list_item">
              <img src={avatar} alt="404" />
              Участие в акциях
            </li>
            <li className="personal__pofile-list_item">
              <img src={avatar} alt="404" />
              Рассылки
            </li>
          </ul>
        </div>
        <button onClick={logOutUser}>asdasd</button>
      </section>
    </>
  );
}

export default PersonalArea;
