import React, { useEffect, useState } from "react";
import "./product.css";
import Footer from "../MainLanding/pages/Footer";
import Header from "../MainLanding/pages/Header";
import { useParams } from "react-router-dom";

function Product() {
  const { name } = useParams();
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://localhost:3000/product");
        const jsonData = await response.json();
        setProducts(jsonData);
      } catch (error) {
        console.error("error fetch product data", error.message);
      }
    };
    fetchData();
  }, []);
  useEffect(() => {
    setFilteredProducts(products.filter((item) => item.name === name));
  }, [products, name]);
  console.log(filteredProducts, name);
  return (
    <div>
      <Header />
      <div className="container">
        {filteredProducts.map((product) => (
          <div key={product.id} product={product}>
            <div className="product">
              <div className="product__preview">
                <div className="product_photos">
                  <div className="product__photo_group">
                    <div className="product__photo"></div>
                    <div className="product__photo"></div>
                    <div className="product__photo"></div>
                    <div className="product__photo"></div>
                  </div>
                  <div className="products__photo_preview"></div>
                </div>

                <div className="product__title">
                  <span>{product.name}</span>
                  <p>{product.cost}</p>
                  <div className=""></div>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
      {/* <Footer /> */}
    </div>
  );
}

export default Product;
