import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function Prod() {
  const { type } = useParams();
  const [products, setProducts] = useState([]);
  useEffect(() => {
    const fetchData = () => {
      fetch("http://localhost:3000/actualProduct")
        .then((response) => response.json())
        .then((jsonData) => setProducts(jsonData));
    };

    fetchData();
  }, []);
  useEffect(() => {
    const findCategories = () => {
      const filteredList = products.filter((item) => item.type === type);
      // console.log(filteredList);
      console.log(products);
    };
    if (products.length > 0) {
      findCategories();
    }
  }, [products, type]);

  return (
    <div>
      <h2>Страница категории: {type}</h2>
      <ul>
        {products.filter((product) => product.type === type).map((filteredProduct))}
      </ul>
    </div>
  );
}

export default Prod;
