import React from "react";
import Header from "../MainLanding/pages/Header";
import { useContext } from "react";
import { CustomContext } from "../utils/Context";
import { useNavigate } from "react-router-dom";
import avatar from "../MainLanding/";

function PersonalArea() {
  const { user, setUser } = useContext(CustomContext);

  const navigate = useNavigate();

  const logOutUser = () => {
    setUser({
      email: "",
    });
    localStorage.removeItem("user");
    navigate("/");
  };

  return (
    <>
      <Header />
      <section className="personal">
        <div className="personal__pofile">
          <div className="personal__pofile-date">
            <img src={location} alt="404" />
            <div className="pofile-date__txt">
              <p>{user.name}</p>
              <p>{user.email}</p>
            </div>
          </div>
        </div>
        <button onClick={logOutUser}>asdasd</button>
      </section>
    </>
  );
}

export default PersonalArea;
