import React, { useState } from "react";
import "./reng.css";

const Renge = () => {
  const [fromValue, setFromValue] = useState(0);
  const [toValue, setToValue] = useState(100);

  const handleFromChange = (e) => {
    setFromValue(parseInt(e.target.value));
  };

  const handleToChange = (e) => {
    setToValue(parseInt(e.target.value));
  };

  return (
    <div className="products__cost">
      <p>Цена ₴</p>
      <label className="products__cost_index" htmlFor="from">
        От:
      </label>
      <input
        type="range"
        id="from"
        value={fromValue}
        min={0}
        max={100}
        step={1}
        onChange={handleFromChange}
      />
      <span>{fromValue}</span>

      <br />

      <label  htmlFor="to">
        До:
      </label>
      <input
        type="range"
        id="to"
        value={toValue}
        min={0}
        max={100}
        step={1}
        onChange={handleToChange}
      />
      <span>{toValue}</span>
    </div>
  );
};

export default Renge;
