import React from "react";
import "../style/auth.css";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";

const SigninSchema = Yup.object().shape({
  email: Yup.string().email("Invalid email").required("Required"),
  password: Yup.string()
    .min(6, "Too Short!")
    .max(50, "Too Long!")
    .required("Required"),
});

function Auth({ AuthActive }) {
  return (
    <div className={AuthActive ? "authoriz active" : "authoriz inactive"}>
      <div className="authoriz__form">
        <h1>Signup</h1>
        <Formik
          initialValues={{ email: "", password: "" }}
          validationSchema={SigninSchema}
          onSubmit={(values) => {
            console.log(values);
          }}
        >
          {({ errors, touched }) => (
            <Form>
              <Field className="authoriz__email" name="email" type="email" />
              {errors.email && touched.email ? (
                <div className="authoriz__email-msg">{errors.email}</div>
              ) : null}
              <Field className="authoriz__password" name="password" />
              {errors.password && touched.password ? (
                <div className="authoriz__password-msg">{errors.password}</div>
              ) : null}
              <button type="submit">Submit</button>
            </Form>
          )}
        </Formik>
      </div>
    </div>
  );
}

export default Auth;
