import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Header from "../MainLanding/pages/Header";
import "./products.css";
// import Footer from "../MainLanding/pages/Footer";

function Prod() {
  const { type } = useParams();
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://localhost:3000/actualProduct");
        const jsonData = await response.json();
        setProducts(jsonData);
      } catch (error) {
        console.error("error fetch product data", error.message);
      }
    };
    fetchData();
  }, []);
  useEffect(() => {
    setFilteredProducts(products.filter((item) => item.type === type));
  }, [products, type]);

  return (
    <section className="products-list">
      <div className="container">
        <Header />

        <div className="products-list__sort">
          <h1>{type}</h1>
          <div className="products__cost">
            <input className="products__cost_index"></input>
            <input
              type="range"
              min="0"
              max="100"
              step="1"
              value="50"
              className="products__cost_slider"
            ></input>
          </div>
          className="products__cost_slider
          <span> Цвет родукта</span>
          <div className="products__memory">
            <span>Встроенная память</span>
            <div className="products__memory_btn_group">
              <button>128 гб</button>
              <button>256 гб</button>
              <button>1 тб</button>
            </div>
          </div>
        </div>
        <div className="products-list__catalog"></div>

        {/* <h2>Страница категории: {type}</h2>
      <ul>
        {filteredProducts.map((product) => (
          <li key={product.id}>{product.name}</li>
        ))}
      </ul> */}
        {/* <Footer /> */}
      </div>
    </section>
  );
}

export default Prod;
