import "./reng.css";

import React, { useState } from "react";

const Renge = () => {
  const [fromValue, setFromValue] = useState(0);
  const [toValue, setToValue] = useState(100);

  const handleSliderChange = (e) => {
    const value = parseInt(e.target.value);
    setFromValue(value);
    setToValue(100 - value);
  };

  return (
    <div className="products__cost">
      <label htmlFor="slider">Диапазон:</label>
      <p>Цена ₴</p>
      <input
        className="products__cost_slider "
        type="range"
        id="slider"
        value={fromValue}
        min={0}
        max={100}
        step={1}
        onChange={handleSliderChange}
      />
      <span>
        От: {fromValue}, До: {toValue}
      </span>
    </div>
  );
};

export default Renge;
