import React, { useState } from "react";
import "./reng.css";

const Renge = () => {
  const [fromValue, setFromValue] = useState(0);
  const [toValue, setToValue] = useState(100);

  const handleFromChange = (e) => {
    setFromValue(parseInt(e.target.value));
  };

  const handleToChange = (e) => {
    setToValue(parseInt(e.target.value));
  };

  return (
    <div className="products__cost">
      <p>Цена ₴</p>
      <div className="products__cost_index_">
        <p className="products__cost_index">{fromValue}</p>
        <p className="products__cost_index">{toValue}</p>
      </div>
      <label htmlFor="from">От:</label>
      <input
        className="products__cost_slider "
        type="range"
        id="from"
        value={fromValue}
        min={0}
        max={100}
        step={1}
        onChange={handleFromChange}
      />

      <label htmlFor="to">До:</label>
      <input
        className="products__cost_slider "
        type="range"
        id="to"
        value={toValue}
        min={0}
        max={100}
        step={1}
        onChange={handleToChange}
      />
    </div>
  );
};

export default Renge;
