import React from "react";
import "../style/like.css";
import { useContext } from "react";
import { CustomContext } from "../../utils/Context";
import { useEffect } from "react";
import { useState } from "react";

function Like() {
  const { favored, setfavored } = useContext(CustomContext);
  const [likeProductsList, setLikeProductList] = useState([]);

  useEffect(() => {
    setLikeProductList(JSON.parse(localStorage.getItem("likes")));
  }, []);

  return (
    <div className="list">
      <div className="asd">
        {likeProductsList.map((item) => (
          <div>
            <span>{item.name}</span>
          </div>
        ))}
        {favored.map((item) => (
          <div className="ad">
            <span>{item.name}</span>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Like;
