import React, { useState } from "react";
import "../style/basket.css";
import Header from "../../MainLanding/pages/Header";
import Footer from "../../MainLanding/pages/Footer";
import { CustomContext } from "../../utils/Context";
import { useContext } from "react";
import { useNavigate } from "react-router-dom";

function Basket() {
  const [caunt, setCaunt] = useState();
  const navigate = useNavigate();
  const { favored, setFavored } = useContext(CustomContext);
  console.log(favored);

  return (
    <>
      <Header />
      <div className="basket-place container">
        <div className="basket-place__title-content">
          <div className="basket-place__title">
            <h2>Ваша корзина</h2>
            <span>{favored.length} товара</span>
          </div>
          <div className="basket-place__tools">
            <span
              className="basket-place__tools-nav"
              onClick={() => {
                navigate("/");
              }}
            >
              Продолжить покупки
            </span>
            <span className="basket-place__tools-clear">Очистить корзину</span>
          </div>
        </div>
        <div className="basket-place__content">
          <ul className="basket-place__content-list">
            {favored.map((item) => {
              const costs = parseFloat(item.cost.replace(/[ ,]/g, ""));
              const buyin = Math.round(costs - costs * item.discount);
              setCaunt((caunt += costs));
              return (
                <li className="basket-place__content-list_item" key={item.id}>
                  <img src={item.img} alt="404" />
                  {item.name}
                  <div className="content-list_item__quantity"></div>
                  <div className="content-list_item__costs">
                    <span>{item.cost}</span>
                    <span>{buyin}</span>
                  </div>
                  <span>{caunt}</span>
                </li>
              );
            })}
          </ul>
        </div>
      </div>
    </>
  );
}

export default Basket;
