import React from "react";
import "../style/auth.css";
import { Formik, Form, Field, ErrorMessage } from "formik";

const SignupSchema = Yup.object().shape({
  password: Yup.string()
    .min(2, "Too Short!")
    .max(50, "Too Long!")
    .required("Required"),

  email: Yup.string().email("Invalid email").required("Required"),
});

function Auth() {
  return (
    <div className="authoriz">
      <div className="authoriz__form">
        <Formik
          initialValues={{ email: "", password: "" }}
          validate={(values) => {
            const errors = {};
            if (!values.email) {
              errors.email = "Required";
            } else if (
              !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
            ) {
              errors.email = "Invalid email address";
            }
            return errors;
          }}
          onSubmit={(values) => {
            console.log(values);
          }}
        >
          {({ isSubmitting }) => (
            <Form>
              <Field className="authoriz__email" type="email" name="email" />
              <ErrorMessage
                className="authoriz__email-mes"
                name="email"
                component="div"
              />
              <Field
                className="authoriz__password"
                type="password"
                name="password"
              />
              <ErrorMessage
                className="authoriz__password"
                name="password"
                component="div"
              />
              <button type="submit" disabled={isSubmitting}>
                Submit
              </button>
            </Form>
          )}
        </Formik>
      </div>
    </div>
  );
}

export default Auth;
