import React from "react";
import "../css/sort.css";
function Sort({ filteredProduct }) {
  function sortDeclining() {
    filteredProduct.sort((a, b) => {
      const costA = parseFloat(a.replace(/[ ,]/g, ""));
      const costB = parseFloat(b.replace(/[ ,]/g, ""));
      return costA - costB;
    });
    
  }
  function sortRising() {}
  function sortStock() {}

  return (
    <div className="products__memory">
      <h2>Отсортировать</h2>
      <ul>
        <li className="products__memory-sort__list-item">
          <p>По убыванию</p>
        </li>
        <li className="products__memory-sort__list-item">
          <p>По возрастанию</p>
        </li>
        <li className="products__memory-sort__list-item">
          <p>Сначала акции</p>
        </li>
      </ul>
    </div>
  );
}

export default Sort;
