import React, { useEffect, useState } from "react";
import "./product.css";
import Footer from "../MainLanding/pages/Footer";
import Header from "../MainLanding/pages/Header";
import { useParams } from "react-router-dom";
import Presentation from "./components/Presentation";

function Product() {
  const { name } = useParams();
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://localhost:3000/product");
        const jsonData = await response.json();
        setProducts(jsonData);
      } catch (error) {
        console.error("error fetch product data", error.message);
      }
    };
    fetchData();
  }, []);
  useEffect(() => {
    setFilteredProducts(products.filter((item) => item.name === name));
  }, [products, name]);
  console.log(filteredProducts, name);
  return (
    <div>
      <Header />

      {filteredProducts.map((product) => (
        <Presentation key={product.id} product={product} className="product" />
      ))}

      {/* <Footer /> */}
    </div>
  );
}

export default Product;
